import React, {Component} from 'react';
import Burger from '../components/Burger/Burger'
import Aux from '../hoc/Aux'
import BurgerControls from '../components/Burger/BurgerControls/BurgerControls';

const INGREDIENT_PRICES = {
        veggies:0.9,
        meat:2,
        cheese:0.4,
        bacon: 0.8
}
class BurgerBuilder extends Component{

    state = {
        ingredients: {
            veggies:0,
            meat:0,
            cheese:0,
            bacon: 0
        }
        ,basePrice: 3
    };

    addIngredientHandler =(type)=>{
        const startCount = this.state.ingredients[type];
        const updatedCount = startCount + 1;
        const updatedIngredients = {
            ...this.state.ingredients
        };
        updatedIngredients[type] = updatedCount;
        const ingrePrice = INGREDIENT_PRICES[type];
        const basePrice = this.state.basePrice;
        const totalPrice = ingrePrice + basePrice
        this.setState({
            ingredients: updatedIngredients,
            basePrice: totalPrice
        })
    };

    removeIngredientHandler =(type)=>{
        const startCount = this.state.ingredients[type];
        if(startCount <= 0){
            return;
        }
        const updatedCount = startCount - 1;
        const updatedIngredients = {
            ...this.state.ingredients
        };
        updatedIngredients[type] = updatedCount;
        const ingrePrice = INGREDIENT_PRICES[type];
        const basePrice = this.state.basePrice;
        const totalPrice = ingrePrice - basePrice
        this.setState({
            ingredients: updatedIngredients,
            basePrice: totalPrice
        })
        console.log(basePrice)
    };

    render(){
        return(
            <Aux>
                <Burger ingredients = {this.state.ingredients} />
                <BurgerControls add = {this.addIngredientHandler}  remove = {this.removeIngredientHandler}/>
            </Aux>
        );
    }
}
export default BurgerBuilder;
